import react from 'react';
import {useHistory} from 'react-router-dom';

function datecalc(fromdate,todate)
{
   const oneday=24*60*60*1000;
   const begin=new Date(fromdate)
   const end=new Date(todate);
   const totalday=Math.round(Math.abs((begin-end)/oneday));
   return totalday;
}

function Hotelcards(props)
{
  const history=useHistory();
    return(        
    <div>
         
         <div className="row singlecol">
            <div className="col-md-4">
           <img src={'http://localhost:3002/collect/image/'+ (props.id)} alt="https://via.placeholder.com/900x500.png?text=roomimage" className="card-image img img-fluid" />       
             </div>
            {/* <div className="col-md-4 ">

                <h5 style={{fontFamily: "'Mate SC', serif"}} >{props.title}</h5>
                <hr></hr>
                <h4 className="alert alert-info">{props.location}</h4>
                <h5>{props.id}</h5>

                <p>{props.description}</p>
                 <h6 style={{fontFamily: "'Kanit', sans-serif"}}>available for {datecalc(props.begin,props.end)} {datecalc(props.begin,props.end)<=1?'day':'days'} </h6>
                 <button onClick={()=>{
                   history.push('/separate/'+(props.id))
                 }} className="btn btn-primary">view details</button>
               </div>


         */}
         </div>

        <p></p>
        
    </div>

  );
}

export default Hotelcards;
